  KEY `vehicle_type_zones_zone_id_foreign` (`zone_id`),
  KEY `vehicle_type_zones_tax_id_foreign` (`tax_id`),
  KEY `vehicle_type_zones_vehicle_type_id_foreign` (`vehicle_type_id`),
